    <script>
        document.addEventListener("alpine:init", () => {
            Alpine.data("sidebar", () => ({
                init() {
                    const selector = document.querySelector('.sidebar ul a[href="' + window.location
                        .href + '"]');
                    if (selector) {
                        selector.classList.add('active');

                        // Set aria-expanded to rotate svg
                        selector.parentNode.parentNode.previousSibling.previousSibling.setAttribute(
                            'aria-expanded', true);
                        // Expand the collapse
                        selector.parentNode.parentNode.classList.remove('hidden');
                    } else {
                        const selector = document.querySelector('#dashboard');
                        selector.classList.add('active');

                    }
                },
            }));
        });
    </script>
